package com.victor.presenter;import android.util.Log;import com.victor.http.annotation.HttpParms;import com.victor.http.data.Request;import com.victor.http.inject.HttpInject;import com.victor.http.module.HttpRequest;import com.victor.http.presenter.impl.BasePresenterImpl;import com.victor.view.JsoupView;/** * Created by victor on 2017/2/8. * 登陆 Prestener实现 */public class JsoupPresenterImpl<H,T> extends BasePresenterImpl<H,T> {	/*Presenter作为中间层，持有View和Model的引用*/	private JsoupView jsoupView;	public JsoupPresenterImpl(JsoupView jsoupView) {		this.jsoupView = jsoupView;	}	@Override	public void onSuccess(T data) {		if (jsoupView == null) return;		if (data == null) {			jsoupView.OnJsoup(null,"no data response");		} else {			jsoupView.OnJsoup(data,"");		}	}	@Override	public void onError(String error) {		if (jsoupView == null) return;		jsoupView.OnJsoup(null,error);	}	@Override	public void detachView() {		jsoupView = null;	}	@HttpParms(method = Request.JSOUP,bodyContentType = HttpRequest.mJsonBodyContentType,			responseCls = String.class,httpFramework = HttpRequest.JSOUP_FRAMEWORK)	@Override	public void sendRequest(String url,H header,T parm) {		HttpInject.inject(this);		super.sendRequest(url,header,parm);	}}