package com.victor.presenter;import com.android.volley.Request;import com.victor.data.PhoneCodeReq;import com.victor.http.annotation.HttpParms;import com.victor.http.inject.HttpInject;import com.victor.http.model.HttpModel;import com.victor.http.module.HttpRequest;import com.victor.http.module.VolleyRequest;import com.victor.http.presenter.impl.BasePresenterImpl;import com.victor.view.LoginView;import com.victor.view.PhoneCodeView;/** * Created by victor on 2017/2/8. * 发送手机验证码 Prestener实现 */public class PhoneCodePresenterImpl<H,T> extends BasePresenterImpl<H,T> {	/*Presenter作为中间层，持有View和Model的引用*/	private PhoneCodeView phoneCodeView;	public PhoneCodePresenterImpl(PhoneCodeView phoneCodeView) {		this.phoneCodeView = phoneCodeView;	}	@Override	public void onSuccess(T data) {		if (phoneCodeView == null) return;		if (data == null) {			phoneCodeView.OnPhoneCode(null,"no data response");		} else {			phoneCodeView.OnPhoneCode(data,"");		}	}	@Override	public void onError(String error) {		if (phoneCodeView == null) return;		phoneCodeView.OnPhoneCode(null,error);	}	@Override	public void detachView() {		phoneCodeView = null;	}	@HttpParms(method = Request.Method.POST,bodyContentType = HttpRequest.mDefaultBodyContentType,			responseCls = PhoneCodeReq.class,httpFramework = HttpRequest.OKHTTP_FRAMEWORK)	@Override	public void sendRequest(String url,H header,T parm) {		HttpInject.inject(this);		super.sendRequest(url,header,parm);	}}