package com.victor.presenter;import com.android.volley.Request;import com.victor.http.annotation.HttpParms;import com.victor.http.inject.HttpInject;import com.victor.http.module.HttpRequest;import com.victor.http.module.VolleyRequest;import com.victor.http.presenter.impl.BasePresenterImpl;import com.victor.data.Weather;import com.victor.view.WeatherView;/** * Created by victor on 2017/2/8. * 天气 Prestener实现 */public class WeatherPresenterImpl <H,T> extends BasePresenterImpl<H,T> {	/*Presenter作为中间层，持有View和Model的引用*/	private WeatherView weatherView;	public WeatherPresenterImpl(WeatherView weatherView) {		this.weatherView = weatherView;	}	@Override	public void onSuccess(T data) {		if (weatherView == null) return;		if (data == null) {			weatherView.OnWeatherInfo(null,"no data response");		} else {			weatherView.OnWeatherInfo(data,"");		}	}	@Override	public void onError(String error) {		if (weatherView == null) return;		weatherView.OnWeatherInfo(null,error);	}	@Override	public void detachView() {		weatherView = null;	}	@HttpParms(method = Request.Method.GET,bodyContentType = HttpRequest.mJsonBodyContentType,			responseCls = Weather.class,httpFramework = HttpRequest.VOLLEY_FRAMEWORK)	@Override	public void sendRequest(String url, H header,T parm) {		HttpInject.inject(this);		super.sendRequest(url,header,parm);	}}